mapper.select:
select * from event_outbox
where status = #{status}
order by create_time asc
limit #{limit}

mapper.update:
update event_outbox
set status = #{targetStatus}
where event_id = #{eventId}
    and status = #{originalStatus}

update event_outbox
set status = #{targetStatus},
    sent_time = now()
where event_id = #{eventId}
  and status = #{originalStatus}

update event_outbox
set status = #{targetStatus},
    retry_count = retry_count + 1
where event_id = #{eventId}
  and status = #{originalStatus}
